import React, { useState, useEffect } from "react";
import Editor from "./components/Editor";
import OutputTerminal from "./components/OutputTerminal";
import Snippets from "./components/Snippets";
import ThemeToggle from "./components/ThemeToggle";
import HelpSection from "./components/HelpSection";
import LanguageSwitcher from "./components/LanguageSwitcher";
import "./styles.css";

const PYTHON_TEMPLATE = "# Write your Python code here\n";
const JS_TEMPLATE = "// JavaScript code editor (Python execution only supported)\n";

function App() {
  const [code, setCode] = useState(() =>
    localStorage.getItem("code") || PYTHON_TEMPLATE
  );
  const [inputs, setInputs] = useState("");
  const [output, setOutput] = useState("");
  const [error, setError] = useState("");
  const [theme, setTheme] = useState(() =>
    localStorage.getItem("theme") || "light"
  );
  const [language, setLanguage] = useState(() =>
    localStorage.getItem("language") || "python"
  );

  // Auto-save code, theme, and language
  useEffect(() => {
    localStorage.setItem("code", code);
  }, [code]);
  useEffect(() => {
    localStorage.setItem("theme", theme);
  }, [theme]);
  useEffect(() => {
    localStorage.setItem("language", language);
  }, [language]);

  // Load shared code if URL contains ?share=ID
  useEffect(() => {
    const params = new URLSearchParams(window.location.search);
    const shareId = params.get("share");
    if (shareId) {
      fetch(`http://localhost:8000/share/${shareId}`)
        .then(res => res.json())
        .then(data => setCode(data.code));
    }
  }, []);

  const runCode = async () => {
    setOutput("Running...");
    setError("");
    if (language !== "python") {
      setOutput("");
      setError("Only Python code can be executed.");
      return;
    }
    const res = await fetch("http://localhost:8000/run", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code, inputs }),
    });
    const data = await res.json();
    setOutput(data.output);
    setError(data.error);
  };

  const shareCode = async () => {
    const res = await fetch("http://localhost:8000/share", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ code }),
    });
    const data = await res.json();
    const url = `${window.location.origin}?share=${data.id}`;
    window.prompt("Share this link:", url);
  };

  const handleLanguageChange = (lang) => {
    setLanguage(lang);
    setCode(lang === "python" ? PYTHON_TEMPLATE : JS_TEMPLATE);
  };

  return (
    <div className={`app ${theme}`}>
      <header>
        <h1>Mini Python IDE</h1>
        <ThemeToggle theme={theme} setTheme={setTheme} />
      </header>
      <div className="main">
        <LanguageSwitcher language={language} setLanguage={handleLanguageChange} />
        <Snippets setCode={setCode} language={language} />
        <Editor code={code} setCode={setCode} theme={theme} language={language} />
        <div className="input-section">
          <label>Input (for input()):</label>
          <textarea value={inputs} onChange={e => setInputs(e.target.value)} />
        </div>
        <div className="btn-row">
          <button className="run-btn" onClick={runCode}>Run ‚ñ∂Ô∏è</button>
          <button className="share-btn" onClick={shareCode}>Share üîó</button>
        </div>
        <OutputTerminal output={output} error={error} />
      </div>
      <HelpSection />
    </div>
  );
}

export default App;
